mapper.select:
			SELECT * FROM (
            SELECT Tb.*, ROWNUM rNum FROM (
            SELECT * FROM inquiryBoard

						where ${searchField} 
						Like '%${searchWord}%'

            START WITH parentIdx=0
            CONNECT BY PRIOR idx = parentIdx
            ORDER SIBLINGS BY idx DESC
            ) Tb )
            WHERE rNum BETWEEN ${start} AND ${end}

select count(*) from inquiryBoard 

			where ${searchField} 
			Like '%${searchWord}%'
